document.addEventListener('DOMContentLoaded',function(){const form=document.querySelector('.contact-form');if(form){const inputs=form.querySelectorAll('input,textarea,select');inputs.forEach(input=>{input.addEventListener('blur',function(){validateField(this)});input.addEventListener('input',function(){if(this.classList.contains('is-invalid')){validateField(this)}})});function validateField(field){const value=field.value.trim();const isRequired=field.hasAttribute('required');field.classList.remove('is-invalid');const errorDiv=field.parentElement.querySelector('.error-message');if(errorDiv && !errorDiv.classList.contains('text-danger')){errorDiv.remove()}if(isRequired && !value){showError(field,'This field is required');return false}if(field.type==='email' && value){const emailRegex=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;if(!emailRegex.test(value)){showError(field,'Please enter a valid email address');return false}}if(field.type==='tel' && value){const phoneDigits=value.replace(/\D/g,'');if(phoneDigits.length < 10){showError(field,'Please enter a valid phone number');return false}}field.classList.add('is-valid');return true}function showError(field,message){field.classList.add('is-invalid');let errorDiv=field.parentElement.querySelector('.error-message:not(.text-danger)');if(!errorDiv){errorDiv=document.createElement('div');errorDiv.className='error-message text-danger small mt-1';field.parentElement.appendChild(errorDiv)}errorDiv.textContent=message}}});